<style lang="scss">

@function px2rem( $px ){
  @return $px*320/$designWidth/20 + rem;
}
$designWidth : 350;

.header {
  display: flex;
  flex-flow: column;
  background-color:#1d2a2d;
  margin: 0 px2rem(-5);
  &>div{
     flex-flow: row;
     height:px2rem(45); 
  } 
  .seek{
    display: flex;
    input{
      flex:4;
      border:none;
      background-color: transparent;
      padding: px2rem(5) px2rem(2);
      // border-bottom: px2rem(1) solid #aba79c;
      text-align: center;
      &[placeholder]{
        color:#fff;
      }
    }
    button{
        flex: 1;
        border: none;
        background-color: transparent;
        align-items: center;
        justify-content: center;
        display: flex;
      svg{
        height: px2rem(30);
        width: px2rem(40);
        color:#fff;
        opacity: 0.5;
      }
    }
  }
  .tag_infor{
        display: flex;
        align-items: center;
        overflow:hidden;
    div{
      flex:1;
      text-align: center;
      svg{
        opacity:0.5;
      }
      &:nth-child(1){
        svg{
          max-width: px2rem(50);
        }
      }
      &:nth-child(2){
        svg{
          max-width: px2rem(30);
          max-height: px2rem(30);
        }
      }
      &:nth-child(3){
        svg{
          max-width: px2rem(30);
          max-height: px2rem(25);
        }
      }
    }

  }
}
</style>